// Enhanced dashboard initialization with real-time features
document.addEventListener('DOMContentLoaded', function() {
    console.log('MWA Core Dashboard initialized');

    // Initialize basic dashboard
    loadDashboardStats();
    loadRecentContacts();
    loadTopSources();
    updateLastUpdated();

    // Check API endpoints
    checkAPIEndpoints();

    // Show connection status modal on first load
    setTimeout(() => {
        const connectionModal = new bootstrap.Modal(document.getElementById('connection-status-modal'));
        connectionModal.show();
    }, 1000);

    // Auto-refresh every 30 seconds (can be toggled)
    window.dashboardInterval = setInterval(function() {
        if (window.realtimeDashboard && window.realtimeDashboard.isAutoRefreshEnabled) {
            loadDashboardStats();
            loadRecentContacts();
        }
    }, 30000);
});

// Real-time control functions
function toggleRealTimeFeatures() {
    if (window.realtimeDashboard) {
        window.realtimeDashboard.toggleAutoRefresh();
    }
}

function requestSystemUpdate() {
    if (window.realtimeDashboard) {
        window.realtimeDashboard.requestManualUpdate();
    }
}

function testWebSocketConnection() {
    if (window.wsClient) {
        console.log('Testing WebSocket connection...');
        window.wsClient.sendMessage({
            type: 'ping',
            data: { timestamp: new Date().toISOString() }
        });
        showNotification('WebSocket test message sent', 'info');
    } else {
        showNotification('WebSocket client not available', 'warning');
    }
}

function checkAPIEndpoints() {
    const endpoints = [
        { name: 'config', url: '/api/v1/config' },
        { name: 'listings', url: '/api/v1/listings' },
        { name: 'contacts', url: '/api/v1/contacts' },
        { name: 'system-endpoint', url: '/api/v1/system/info' }
    ];

    endpoints.forEach(endpoint => {
        const statusElement = document.getElementById(`${endpoint.name.replace('-', '')}-status`);
        if (statusElement) {
            fetch(endpoint.url)
                .then(response => {
                    if (response.ok || response.status === 401) {
                        statusElement.className = 'badge bg-success';
                        statusElement.textContent = 'Online';
                    } else {
                        statusElement.className = 'badge bg-warning';
                        statusElement.textContent = 'Warning';
                    }
                })
                .catch(() => {
                    statusElement.className = 'badge bg-danger';
                    statusElement.textContent = 'Offline';
                });
        }
    });
}

function testAllConnections() {
    showNotification('Testing all connections...', 'info');
    checkAPIEndpoints();
    testWebSocketConnection();
}

// Enhanced notification system
function showNotification(message, type = 'info', duration = 5000) {
    const toastContainer = document.querySelector('.toast-container');
    const toastId = 'toast-' + Date.now();

    const toast = document.createElement('div');
    toast.id = toastId;
    toast.className = `toast align-items-center text-white bg-${type} border-0`;
    toast.setAttribute('role', 'alert');
    toast.innerHTML = `
        <div class="d-flex">
            <div class="toast-body">
                ${message}
            </div>
            <button type="button" class="btn-close btn-close-white me-2 m-auto" data-bs-dismiss="toast"></button>
        </div>
    `;

    toastContainer.appendChild(toast);

    const bsToast = new bootstrap.Toast(toast, { delay: duration });
    bsToast.show();

    // Remove toast element after it's hidden
    toast.addEventListener('hidden.bs.toast', () => {
        toast.remove();
    });
}

// Performance monitoring
function updatePerformanceMetrics() {
    // This would be called by the real-time dashboard
    const now = new Date();
    const metrics = {
        timestamp: now,
        cpu: Math.random() * 100, // Placeholder - would come from API
        memory: Math.random() * 100, // Placeholder - would come from API
        requests: Math.floor(Math.random() * 100) // Placeholder
    };

    // Update displays
    document.getElementById('cpu-usage').textContent = metrics.cpu.toFixed(1) + '%';
    document.getElementById('memory-usage').textContent = metrics.memory.toFixed(1) + '%';
}

